mapper.select:
SELECT * FROM COMPETITION ,DUAL WHERE
c_name LIKE '%'||#{c_name,jdbcType=VARCHAR}||'%'
AND area LIKE '%'||#{area,jdbcType=VARCHAR}||'%'
AND TO_CHAR(deadline,'mmdd') >= #{deadline,jdbcType=VARCHAR}
AND candidate LIKE '%'||#{candidate,jdbcType=VARCHAR}||'%'
AND organizer2 in
#{item}
AND s_year <= #{s_year,jdbcType=VARCHAR} AND deadline >= #{s_year,jdbcType=VARCHAR}
AND b_year LIKE '%'||#{b_year,jdbcType=VARCHAR}||'%'
ORDER BY R_DATE DESC

